INSERT INTO `wp_posts` VALUES (64,1,'2024-06-17 08:51:51','2024-06-16 23:51:51','<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">ST上昇型心筋梗塞（STEMI）についての急性期の理学療法士が必要な知識</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">1. STEMIの病態生理</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>定義</strong>: ST上昇型心筋梗塞（STEMI）は、冠状動脈の完全閉塞により心筋が急激に壊死する病態で、心電図におけるSTセグメントの上昇が特徴です。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>病因</strong>: 冠動脈のアテローム性動脈硬化プラークの破裂と血栓形成が主な原因です。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">2. 症状と診断</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>症状</strong>: 激しい胸痛（典型的には左側胸部に圧迫感）、放散痛（肩、腕、顎など）、呼吸困難、冷汗、悪心・嘔吐、意識喪失など。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>診断</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>心電図（ECG）</strong>: STセグメントの上昇。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>血清マーカー</strong>: トロポニンT/I、CK-MBの上昇。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>画像診断</strong>: 冠動脈造影、心エコー。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">3. 急性期の治療</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>再灌流療法</strong>: 最も重要な治療は迅速な再灌流療法（血栓溶解療法、経皮的冠動脈形成術[PCI]、冠動脈バイパス手術[CABG]）です。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>薬物療法</strong>: 抗血小板薬（アスピリン、クロピドグレル）、抗凝固薬（ヘパリン）、ニトログリセリン、β遮断薬、ACE阻害薬、スタチンなど。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">4. 理学療法士の役割</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>バイタルサインの監視</strong>: 血圧、心拍数、酸素飽和度、呼吸数を綿密に監視し、運動の安全性を確保します。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動耐容能の評価</strong>: 心肺機能評価を実施し、個々の患者に合わせたリハビリプログラムを設定します。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動強度の調整</strong>: 運動強度を適切に調整し、過負荷を避けるようにします。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>禁忌事項の理解</strong>: 不安定な血行動態、再灌流障害、重篤な不整脈の存在時には運動を制限または中止します。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">入院から退院までのタイムトレンドとリハビリの内容</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">急性期（発症から1週間）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 心機能の安定化、早期離床、合併症予防。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>1-3日目</strong>:<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>安静度の高い状態でのリハビリ開始。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>ベッド上での軽い四肢運動、深呼吸練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>バイタルサインの綿密な監視。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>早期離床が可能な場合、座位保持やベッドサイドでの座位練習。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>4-7日目</strong>:<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>状態が安定していれば、立ち上がり、短時間の歩行練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>バイタルサインの監視を継続し、運動強度を段階的に増加。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">回復期（1週目以降から退院まで）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 身体機能の改善、自立した日常生活動作（ADL）の再獲得、退院準備。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>第2週目</strong>:<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>室内での歩行練習、トレッドミルやエルゴメーターでの低強度運動。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>ADL訓練：洗面、着替え、トイレ動作の練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>軽い抵抗運動や筋力強化運動。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>第3週目以降</strong>:<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>運動強度を徐々に増加。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>中等度の有酸素運動、階段昇降訓練。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>自宅での運動プログラムの指導と教育。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">退院準備期（3週目以降）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 退院後の生活への適応、長期的な心肺機能の改善。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>外来リハビリの計画</strong>: 退院後の外来リハビリテーションや地域リハビリテーションセンターへの紹介。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>家庭での運動プログラム</strong>: 個々の状態に合わせた運動プログラムを作成し、家族も含めて指導。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>生活習慣の改善</strong>: 食事、運動、ストレス管理、禁煙などの指導。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>定期フォローアップ</strong>: 定期的な医師の診察や理学療法士のフォローアップを受ける。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">まとめ</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>ST上昇型心筋梗塞（STEMI）患者に対するリハビリテーションは、患者の心機能や合併症の状態に応じて段階的に進められます。急性期には心機能の安定化と早期離床、回復期には身体機能の改善とADLの再獲得、退院準備期には自立した生活への適応を目指します。理学療法士は患者のバイタルサインを綿密に監視し、安全で効果的なリハビリテーションを提供することが求められます。また、退院後も継続的なリハビリと生活習慣の改善が重要です。</p>\n<!-- /wp:paragraph -->','ST上昇型心筋梗塞（STEMI）','','inherit','closed','closed','','63-revision-v1','','','2024-06-17 08:51:51','2024-06-16 23:51:51','',63,'https://acuterehabililocal.local/?p=64',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (65,1,'2024-06-17 08:54:38','2024-06-16 23:54:38','<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">狭心症についての急性期の理学療法士が必要な知識</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">1. 狭心症の病態生理</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>定義</strong>: 狭心症は、心筋への血流不足により胸痛や圧迫感が生じる状態です。冠動脈の一過性の狭窄やスパズムによって発生します。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>種類</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>安定狭心症</strong>: 予測可能な活動やストレスにより症状が出現し、休息やニトログリセリンで改善する。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>不安定狭心症</strong>: 突然発症し、安静時にも症状が出現する。心筋梗塞の前兆となることが多い。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>異型狭心症（プリンツメタル狭心症）</strong>: 冠動脈の一過性のスパズムにより発生し、主に安静時に発症する。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">2. 症状と診断</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>症状</strong>: 胸痛、圧迫感、息切れ、放散痛（肩、腕、背中、顎など）、冷汗、めまい、悪心など。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>診断</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>心電図（ECG）</strong>: 症状時に一過性のSTセグメントの変化（上昇または下降）が見られることがあります。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>負荷試験</strong>: 運動負荷試験や薬剤負荷試験で心筋虚血を誘発。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>画像診断</strong>: 冠動脈CT、冠動脈造影など。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">3. 治療法</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>薬物療法</strong>: ニトログリセリン、β遮断薬、カルシウム拮抗薬、抗血小板薬（アスピリン）、スタチンなど。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>再灌流療法</strong>: PCI（経皮的冠動脈インターベンション）、CABG（冠動脈バイパス手術）。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>生活習慣の改善</strong>: 食事、運動、禁煙、ストレス管理など。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">4. 理学療法士の役割</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>バイタルサインの監視</strong>: 血圧、心拍数、酸素飽和度、呼吸数を綿密に監視。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動耐容能の評価</strong>: 心肺機能評価を実施し、適切な運動強度を設定。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動強度の調整</strong>: 無理のない範囲で運動を進め、症状が出現した場合は即座に中止。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>禁忌事項の理解</strong>: 安静が必要な状況や運動制限がある場合（不安定狭心症、重篤な不整脈など）。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">入院から退院までのタイムトレンドとリハビリの内容</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">入院初期（1-3日）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 心機能の安定化、早期離床、合併症予防。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>安静度の高い運動</strong>: ベッド上での軽い四肢運動や深呼吸練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>バイタルサインの監視</strong>: 軽い運動の前後にバイタルサインをチェック。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>早期離床</strong>: 座位保持、ベッドサイドでの座位練習。状態が安定していれば、立ち上がりや短時間の歩行練習。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">入院中期（4-7日）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 身体機能の改善、基本的な日常生活動作（ADL）の再獲得。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>軽い有酸素運動</strong>: 室内での短時間の歩行、トレッドミルやエルゴメーターの低強度運動。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>ADL訓練</strong>: 洗面、着替え、トイレ動作の練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>筋力トレーニング</strong>: 軽い抵抗運動や筋力強化運動。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">入院後期（8-14日）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 自立した日常生活の再構築、退院準備。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動強度の増加</strong>: 心肺機能を高めるための中等度の有酸素運動、階段昇降訓練。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>自宅での運動指導</strong>: 退院後も継続できる運動プログラムの作成。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>教育と指導</strong>: 狭心症再発予防のための生活習慣改善（食事、運動、ストレス管理、禁煙など）について指導。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">退院準備期（15日以降）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 退院後の生活への適応、長期的な心肺機能の改善。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>外来リハビリの計画</strong>: 退院後の外来リハビリテーションや地域リハビリテーションセンターへの紹介。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>家庭での運動プログラム</strong>: 個々の状態に合わせた運動プログラムを作成し、家族も含めて指導。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>生活習慣の改善</strong>: 食事、運動、ストレス管理、禁煙などの指導。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>定期フォローアップ</strong>: 定期的な医師の診察や理学療法士のフォローアップを受ける。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">まとめ</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>狭心症患者に対するリハビリテーションは、患者の心機能や合併症の状態に応じて段階的に行います。急性期には心機能の安定化と早期離床、中期には身体機能の改善とADLの再獲得、後期には自立した生活への適応を目指します。理学療法士は患者のバイタルサインを綿密に監視し、安全で効果的なリハビリテーションを提供することが求められます。また、退院後も継続的なリハビリと生活習慣の改善が重要です。</p>\n<!-- /wp:paragraph -->','狭心症','','publish','open','open','','%e7%8b%ad%e5%bf%83%e7%97%87','','','2024-06-26 07:43:37','2024-06-25 22:43:37','',0,'https://acuterehabililocal.local/?p=65',0,'post','',0);
INSERT INTO `wp_posts` VALUES (66,1,'2024-06-17 08:54:38','2024-06-16 23:54:38','<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">狭心症についての急性期の理学療法士が必要な知識</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">1. 狭心症の病態生理</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>定義</strong>: 狭心症は、心筋への血流不足により胸痛や圧迫感が生じる状態です。冠動脈の一過性の狭窄やスパズムによって発生します。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>種類</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>安定狭心症</strong>: 予測可能な活動やストレスにより症状が出現し、休息やニトログリセリンで改善する。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>不安定狭心症</strong>: 突然発症し、安静時にも症状が出現する。心筋梗塞の前兆となることが多い。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>異型狭心症（プリンツメタル狭心症）</strong>: 冠動脈の一過性のスパズムにより発生し、主に安静時に発症する。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">2. 症状と診断</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>症状</strong>: 胸痛、圧迫感、息切れ、放散痛（肩、腕、背中、顎など）、冷汗、めまい、悪心など。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>診断</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>心電図（ECG）</strong>: 症状時に一過性のSTセグメントの変化（上昇または下降）が見られることがあります。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>負荷試験</strong>: 運動負荷試験や薬剤負荷試験で心筋虚血を誘発。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>画像診断</strong>: 冠動脈CT、冠動脈造影など。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">3. 治療法</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>薬物療法</strong>: ニトログリセリン、β遮断薬、カルシウム拮抗薬、抗血小板薬（アスピリン）、スタチンなど。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>再灌流療法</strong>: PCI（経皮的冠動脈インターベンション）、CABG（冠動脈バイパス手術）。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>生活習慣の改善</strong>: 食事、運動、禁煙、ストレス管理など。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">4. 理学療法士の役割</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>バイタルサインの監視</strong>: 血圧、心拍数、酸素飽和度、呼吸数を綿密に監視。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動耐容能の評価</strong>: 心肺機能評価を実施し、適切な運動強度を設定。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動強度の調整</strong>: 無理のない範囲で運動を進め、症状が出現した場合は即座に中止。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>禁忌事項の理解</strong>: 安静が必要な状況や運動制限がある場合（不安定狭心症、重篤な不整脈など）。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">入院から退院までのタイムトレンドとリハビリの内容</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">入院初期（1-3日）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 心機能の安定化、早期離床、合併症予防。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>安静度の高い運動</strong>: ベッド上での軽い四肢運動や深呼吸練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>バイタルサインの監視</strong>: 軽い運動の前後にバイタルサインをチェック。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>早期離床</strong>: 座位保持、ベッドサイドでの座位練習。状態が安定していれば、立ち上がりや短時間の歩行練習。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">入院中期（4-7日）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 身体機能の改善、基本的な日常生活動作（ADL）の再獲得。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>軽い有酸素運動</strong>: 室内での短時間の歩行、トレッドミルやエルゴメーターの低強度運動。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>ADL訓練</strong>: 洗面、着替え、トイレ動作の練習。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>筋力トレーニング</strong>: 軽い抵抗運動や筋力強化運動。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">入院後期（8-14日）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 自立した日常生活の再構築、退院準備。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動強度の増加</strong>: 心肺機能を高めるための中等度の有酸素運動、階段昇降訓練。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>自宅での運動指導</strong>: 退院後も継続できる運動プログラムの作成。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>教育と指導</strong>: 狭心症再発予防のための生活習慣改善（食事、運動、ストレス管理、禁煙など）について指導。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">退院準備期（15日以降）</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>目標</strong>: 退院後の生活への適応、長期的な心肺機能の改善。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>リハビリ内容</strong>:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>外来リハビリの計画</strong>: 退院後の外来リハビリテーションや地域リハビリテーションセンターへの紹介。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>家庭での運動プログラム</strong>: 個々の状態に合わせた運動プログラムを作成し、家族も含めて指導。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>生活習慣の改善</strong>: 食事、運動、ストレス管理、禁煙などの指導。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>定期フォローアップ</strong>: 定期的な医師の診察や理学療法士のフォローアップを受ける。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">まとめ</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>狭心症患者に対するリハビリテーションは、患者の心機能や合併症の状態に応じて段階的に行います。急性期には心機能の安定化と早期離床、中期には身体機能の改善とADLの再獲得、後期には自立した生活への適応を目指します。理学療法士は患者のバイタルサインを綿密に監視し、安全で効果的なリハビリテーションを提供することが求められます。また、退院後も継続的なリハビリと生活習慣の改善が重要です。</p>\n<!-- /wp:paragraph -->','狭心症','','inherit','closed','closed','','65-revision-v1','','','2024-06-17 08:54:38','2024-06-16 23:54:38','',65,'https://acuterehabililocal.local/?p=66',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (67,1,'2024-06-17 08:55:40','2024-06-16 23:55:40','<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">CK-MBについての急性期の理学療法士が必要な知識</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">1. CK-MBとは</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>定義</strong>: CK-MB（クレアチンキナーゼ-MB）は、心筋特異的な酵素であり、心筋細胞の損傷時に血中に放出されます。心筋梗塞などの心筋損傷の診断に用いられます。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>臨床的意義</strong>: CK-MBは急性心筋梗塞の早期診断と、再梗塞の評価に重要です。心筋梗塞発症後、4-6時間で上昇し、18-24時間でピークに達し、その後48-72時間で正常値に戻ります。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">2. 心筋損傷に関連する病態</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>心筋梗塞</strong>: CK-MBの上昇は心筋梗塞の指標であり、再灌流療法の効果を評価するためにも使用されます。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>その他の心筋障害</strong>: 心筋炎、心外傷、心臓手術後などでもCK-MBが上昇することがあります。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">3. 理学療法士の役割</h4>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><strong>評価と監視</strong>: CK-MB値の推移を理解し、心筋損傷の程度や回復の進行を評価します。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>バイタルサインの監視</strong>: 血圧、心拍数、酸素飽和度、呼吸数などのバイタルサインを綿密に監視し、安全なリハビリテーションを提供します。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>運動強度の調整</strong>: 心筋の回復状況に応じて、適切な運動強度を設定し、無理のない範囲でリハビリを進めます。</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><strong>禁忌事項の理解</strong>: 心筋損傷のリスクが高い状況や、CK-MBが高値を示している場合は、過度な運動を避けるようにします。</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">まとめ</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>CK-MBは心筋損傷の指標として重要であり、急性期における心筋梗塞の診断と治療効果の評価に用いられます。理学療法士はCK-MBの推移を理解し、バイタルサインを綿密に監視しながら、適切な運動強度を設定してリハビリテーションを進めることが求められます。リハビリテーションは急性期から回復期、退院準備期にかけて段階的に進められ、患者の心機能や全身状態に応じたプログラムを提供します。退院後も継続的なリハビリと生活習慣の改善が重要です。</p>\n<!-- /wp:paragraph -->','CK-MB（クレアチンキナーゼ-MB）','','publish','open','open','','ck-mb%ef%bc%88%e3%82%af%e3%83%ac%e3%82%a2%e3%83%81%e3%83%b3%e3%82%ad%e3%83%8a%e3%83%bc%e3%82%bc-mb%ef%bc%89','','','2024-06-26 07:43:29','2024-06-25 22:43:29','',0,'https://acuterehabililocal.local/?p=67',0,'post','',0);
