INSERT INTO `wp_posts` VALUES (125,1,'2024-06-21 08:41:18','2024-06-20 23:41:18','<!-- wp:template-part {\"slug\":\"header\",\"theme\":\"twentytwentythree-child\",\"tagName\":\"header\"} /-->\n\n<!-- wp:group {\"tagName\":\"main\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|70\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<main class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--50);margin-bottom:var(--wp--preset--spacing--70)\"><!-- wp:heading {\"level\":1,\"align\":\"wide\",\"style\":{\"spacing\":{\"margin\":{\"bottom\":\"var:preset|spacing|60\"}}}} -->\n<h1 class=\"wp-block-heading alignwide\" style=\"margin-bottom:var(--wp--preset--spacing--60)\">救急リハビリチャンネル</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"className\":\"main\\u002d\\u002dintro-text\"} -->\n<p class=\"main--intro-text\">ようこそ、急性期の理学療法士のための情報提供サイトへ<br><br>このサイトは、急性期医療に従事する理学療法士の皆様に特化したサイトです。<br><br>現在の医療情報サイトは看護師や医師向けの内容が中心で、理学療法士向けの情報が不足しています。<br>そのため、現状、理学療法士は論文を漁って介入内容を検討してます。<br>しかし、一日何人もの患者さんに介入するとなると、そのような時間はなく、業務後に上司に教えてもらったり、夜遅くまで介入方法を調べるというのが現状です。<br>今の現状は打破するために、読みやすい、信頼できる情報源が必要だと思いました。<br>そこで、理学療法士の視点から必要な情報を集約し、皆様の現場での業務を支援するためにこのサイトを開設しました。<br><br>新人の理学療法士の方々が、<br>エビデンスに基づいたアプローチを行う際の不安を少しでも解消できるよう、信頼性の高い情報や実践的なアドバイスを提供するよう心がけます。<br><br>急性期の患者様に対する効果的な方法を学び、自信を持って介入を行えるよう、このサイトをご活用ください。<br><br>理学療法士の皆様のご活躍を心より応援いたします。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"fontSize\":\"large\"} -->\n<h2 class=\"wp-block-heading has-text-align-center has-large-font-size\"><span class=\"material-symbols-outlined\">book_2<br></span>用語辞典</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">急性期の理学療法士に必要な用語をまとめました。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"lightbox\":{\"enabled\":false},\"id\":153,\"width\":\"505px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"custom\",\"align\":\"center\",\"className\":\"banner-dictionary\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized banner-dictionary\"><a href=\"https://acuterehabililocal.local/word\"><img src=\"https://acuterehabililocal.local/wp-content/uploads/2024/06/banner_dictionary.png\" alt=\"\" class=\"wp-image-153\" style=\"width:505px;height:auto\"/></a></figure>\n<!-- /wp:image -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"fontSize\":\"large\"} -->\n<h2 class=\"wp-block-heading has-text-align-center has-large-font-size\"><span class=\"material-symbols-outlined\">article<br></span>新着記事</h2>\n<!-- /wp:heading -->\n\n<!-- wp:query {\"queryId\":0,\"query\":{\"pages\":0,\"offset\":0,\"postType\":\"post\",\"order\":\"desc\",\"orderBy\":\"date\",\"author\":\"\",\"search\":\"\",\"exclude\":[],\"sticky\":\"\",\"inherit\":true,\"perPage\":5},\"align\":\"wide\",\"className\":\"home\\u002d\\u002dlatest-articles__wrapper\",\"layout\":{\"type\":\"constrained\",\"contentSize\":\"1000px\"}} -->\n<div class=\"wp-block-query alignwide home--latest-articles__wrapper\"><!-- wp:post-template {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":\"0\"}},\"layout\":{\"type\":\"default\"}} -->\n<!-- wp:post-featured-image {\"isLink\":true,\"width\":\"100%\",\"height\":\"clamp(15vw, 30vh, 400px)\",\"align\":\"wide\"} /-->\n\n<!-- wp:post-title {\"level\":3,\"isLink\":true,\"fontSize\":\"large\"} /-->\n\n<!-- wp:post-excerpt {\"moreText\":\"\"} /-->\n\n<!-- wp:post-date {\"isLink\":true} /-->\n\n<!-- wp:spacer {\"height\":\"var(\\u002d\\u002dwp\\u002d\\u002dpreset\\u002d\\u002dspacing\\u002d\\u002d40)\"} -->\n<div style=\"height:var(--wp--preset--spacing--40)\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n<!-- /wp:post-template -->\n\n<!